import { isClient } from "./app.mts";
import { unSignal } from "./stateble.mts";
export const headMeta = {
    title: "",
    meta: [],
    link: [],
    scripts: [],
};
export const setDocumentTitle = (value) => {
    headMeta.title = unSignal(value);
    if (isClient) {
        document.title = headMeta.title;
    }
};
